/////////////////////////////////////////////////
//
// libZRTP support
//

#undef HAS_LIBZRTP

#if defined(HAS_LIBZRTP) && !defined(_WIN32_WCE)

  #define OPAL_ZRTP  1

  #if defined(_MSC_VER)

    #ifdef _DEBUG
      #define LIBZRTP_LIBRARY "/Debug/libzrtp.lib"
    #else
      #define LIBZRTP_LIBRARY "/Release/libzrtp.lib"
    #endif

    #pragma comment(lib, LIBZRTP_LIBRARY)

    #pragma include_alias("WINCONFIG/config.h",         "/srtp_win32k/WINCONFIG/config.h")
    #pragma include_alias("WINCONFIG\config.h",         "/srtp_win32k/WINCONFIG/config.h")

    #pragma include_alias(<zrtp.h>,                     </include/zrtp.h>)
    #pragma include_alias(<zrtp_log.h>,                 </include/zrtp_log.h>)
    #pragma include_alias(<zrtp_types.h>,               </include/zrtp_types.h>)   
    #pragma include_alias(<zrtp_crypto.h>,              </include/zrtp_crypto.h>)
    #pragma include_alias(<zrtp_protocol.h>,            </include/zrtp_protocol.h>)
    #pragma include_alias(<zrtp_string.h>,              </include/zrtp_string.h>)
    #pragma include_alias(<zrtp_iface.h>,               </include/zrtp_iface.h>)
    #pragma include_alias(<zrtp_error.h>,               </include/zrtp_error.h>)
    #pragma include_alias(<zrtp_net.h>,                 </include/zrtp_net.h>)
    #pragma include_alias(<zrtp_system.h>,              </include/zrtp_system.h>)
    #pragma include_alias("bn.h",                       "/bnlib/bn.h")
    #pragma include_alias(<bn.h>,                       </bnlib/bn.h>)
    #pragma include_alias("bnsize00.h",                 "/bnlib/bnsize00.h")
    #pragma include_alias(<bnsize00.h>,                 </bnlib/bnsize00.h>)

    #pragma include_alias("aes.h",                      "/bgaes/aes.h")
    #pragma include_alias("sha2.h",                     "/bgaes/sha2.h")

    #pragma include_alias("zrtp_win_config.h",          "/config/zrtp_win_config.h")


    // ZRTP includes SRTP, so include SRTP headers

    #pragma include_alias("srtp.h",              "/srtp_win32k/include/srtp.h")
    #pragma include_alias(<srtp.h>,              </srtp_win32k/include/srtp.h>)
    #pragma include_alias("srtp/srtp.h",         "/srtp_win32k/include/srtp.h")
    #pragma include_alias(<srtp/include/srtp.h>, </srtp_win32k/include/srtp.h>)
    #pragma include_alias("crypto_kernel.h",     "/srtp_win32k/crypto/include/crypto_kernel.h")
    #pragma include_alias("rdbx.h",              "/srtp_win32k/crypto/include/rdbx.h")
    #pragma include_alias("rdb.h",               "/srtp_win32k/crypto/include/rdb.h")
    #pragma include_alias("integers.h",          "/srtp_win32k/crypto/include/integers.h")

    // end of SRTP headers

  #endif
#endif

