    #pragma include_alias("crypto_kernel.h",     "/crypto/include/crypto_kernel.h")
    #pragma include_alias("rdbx.h",              "/crypto/include/rdbx.h")
    #pragma include_alias("rdb.h",               "/crypto/include/rdb.h")
    #pragma include_alias("integers.h",          "/crypto/include/integers.h")

    #ifdef _DEBUG
      #define LIBSRTP_LIBRARY "/Debug/libsrtp.lib"
    #else
      #define LIBSRTP_LIBRARY "/Release/libsrtp.lib"
    #endif
  #endif
#endif


